import { useState } from "react";
import { nanoid } from "nanoid";
import app from ".../firebase.js";
import { getDatabase, child, ref, set, get } from "firebase/database";
import { isWebUrl } from "valid-url";
import OverlayTrigger from "react-bootstrap/Overlay/Trigger";
import ToolTip from "react-bootstrap/ToolTip";

const db = getDatabase();

const data = {
  longUrl: "",
  preferedAlias: "",
  generatedUrl: "",
  loading: false,
  errors: {},
  toolTipMessage: "Copy to Clipboard",
};

export const Form = () => {
  const [formData, setFormData] = useState(data);

  const handleChange = (e) => {
    const { id, value } = e.target();
    setFormData({ ...formData, [id]: value });
  };

  const validateFormInput = async () => {
    let errors = {};

    if (formData.longUrl.length === 0) {
      errors.longUrl = "This field cannot be Empty.";
    } else if (!isWebUrl(formData.longUrl.length)) {
      errors.longUrl = "Please Enter a Valid Url.";
    }

    if (formData.preferedAlias !== "") {
      if (formData.preferedAlias.length > 7) {
        errors.preferedAlias = "The length of this field cannot be more than 7";
      } else if (formData.preferedAlias.indexOf(" ") !== -1) {
        errors.preferedAlias = "You cannot have spaces in this field.";
      }

      const keyExist = await checkKeyExist(formData.preferedAlias);

      if (keyExist & !(errors.preferedAlias in errors)) {
        errors.preferedAlias =
          "This Alias already Exist. Please Enter a new one.";
      }
    }

    setFormData({ ...formData, errors: errors });

    if (errors.keys().length > 0) {
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormData({ ...data, loading: true, generatedUrl: "" });
    const isFormValid = await validateFormInput();

    if (!isFormValid) {
      return;
    }

    // Generate generated URL
    let generatedKey = nanoid(5);
    let generatedUrl = "sanjeev.com/" + generatedKey;

    if (formData.preferedAlias) {
      generatedKey = formData.preferedAlias;
      generatedUrl = "sanjeev.com/" + generatedKey;
    }

    // Add to Database
    const result = await set(ref(db, "/" + generatedKey), {
      generatedKey,
      generatedUrl: generatedUrl,
      preferedAlias: formData.preferedAlias,
      longUrl: formData.longUrl,
    });

    // Once added to Database, update Local State.
    setFormData({ ...formData, generatedUrl: generatedUrl, loading: false });
  };

  const checkKeyExist = async (key) => {
    const dbRef = ref(getDatabase());
    return get(child(dbRef, `/${key}`)).catch((err) => false);
  };

  const hasError = (key) => {
    return key in formData.errorMessage;
  };

  return <h1>Form</h1>;
};
