import nanoid from "uuid";

const data = {
  longUrl: "",
  preferedAlias: "",
  generatedUrl: "",
  loading: false,
  errors: [],
  errorMessage: {},
  toolTipMessage: "Copy to Clipboard",
};

export const Form = () => {
  const [formdata, setFormData] = useState(data);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormData({ ...data, loading: true, generatedUrl: "" });
    const isFormValid = await validateFormInput();

    if (!isFormValid) {
    }

    let generatedUrl = "";
    if (data.preferedAlias) {
      generatedUrl = "sanjeev.com/" + data.preferedAlias;
    } else {
      const preferedAlias = nanoid(6);
      generatedUrl = "sanjev.com/" + preferedAlias;
    }
  };

  return <h1>Form</h1>;
};
